def count_pages(total_digits):
    pages = 0
    digits_used = 0

    # Count digits for 1-digit pages (1-9)
    if digits_used + 9 <= total_digits:
        pages += 9
        digits_used += 9
    else:
        return pages + (total_digits - digits_used)

    # Count digits for 2-digit pages (10-99)
    if digits_used + 180 <= total_digits:
        pages += 90
        digits_used += 180
    else:
        return pages + (total_digits - digits_used) // 2

    # Count digits for 3-digit pages (100-999)
    if digits_used + 2700 <= total_digits:
        pages += 900
        digits_used += 2700
    else:
        return pages + (total_digits - digits_used) // 3

    # Count digits for 4-digit pages (1000-9999)
    if digits_used + 36000 <= total_digits:
        pages += 9000
        digits_used += 36000
    else:
        return pages + (total_digits - digits_used) // 4

    return pages

print(count_pages(2886))
